UPDATE `script_texts` SET `entry` = -1409008,`content_default` = 'Behold Ragnaros, the Firelord! He who was ancient when this world was young! Bow before him, mortals! Bow before your ending!',`content_loc1` = NULL,`content_loc2` = NULL,`content_loc3` = NULL,`content_loc4` = NULL,`content_loc5` = NULL,`content_loc6` = NULL,`content_loc7` = NULL,`content_loc8` = NULL,`sound` = 8040,`type` = 1,`language` = 0,`emote` = 0,`comment` = 'ragnaros SAY_SUMMON_MAJ' WHERE `script_texts`.`entry` = -1409008;
UPDATE `script_texts` SET `entry` = -1409009,`content_default` = 'TOO SOON! YOU HAVE AWAKENED ME TOO SOON, EXECUTUS! WHAT IS THE MEANING OF THIS INTRUSION?',`content_loc1` = NULL,`content_loc2` = NULL,`content_loc3` = NULL,`content_loc4` = NULL,`content_loc5` = NULL,`content_loc6` = NULL,`content_loc7` = NULL,`content_loc8` = NULL,`sound` = 8043,`type` = 1,`language` = 0,`emote` = 0,`comment` = 'ragnaros SAY_ARRIVAL1_RAG' WHERE `script_texts`.`entry` = -1409009;
UPDATE `script_texts` SET `entry` = -1409010,`content_default` = 'These mortal infidels, my lord! They have invaded your sanctum, and seek to steal your secrets!',`content_loc1` = NULL,`content_loc2` = NULL,`content_loc3` = NULL,`content_loc4` = NULL,`content_loc5` = NULL,`content_loc6` = NULL,`content_loc7` = NULL,`content_loc8` = NULL,`sound` = 8041,`type` = 1,`language` = 0,`emote` = 0,`comment` = 'ragnaros SAY_ARRIVAL2_MAJ' WHERE `script_texts`.`entry` = -1409010;
UPDATE `script_texts` SET `entry` = -1409011,`content_default` = 'FOOL! YOU ALLOWED THESE INSECTS TO RUN RAMPANT THROUGH THE HALLOWED CORE, AND NOW YOU LEAD THEM TO MY VERY LAIR? YOU HAVE FAILED ME, EXECUTUS! JUSTICE SHALL BE MET, INDEED!',`content_loc1` = NULL,`content_loc2` = NULL,`content_loc3` = NULL,`content_loc4` = NULL,`content_loc5` = NULL,`content_loc6` = NULL,`content_loc7` = NULL,`content_loc8` = NULL,`sound` = 8044,`type` = 1,`language` = 0,`emote` = 0,`comment` = 'ragnaros SAY_ARRIVAL3_RAG' WHERE `script_texts`.`entry` = -1409011;
